import React, { useState } from "react";

const App = () => {
  const green = "#8e44ad";
  const [bg, setBg] = useState(green);
  const [name,setName] = useState('click Me');
  const [name1 , setName1] = useState('Aweosme');
  const bgChange = ()=>{
    let newBg = "#34485e";
    setBg(newBg);
    setName('Awesome');
    setName1('click me');
  }

  return (
    <>
      <div style = {{backgroundColor:bg}}>
        <button onClick = {bgChange}> {name,name1}</button>
      </div>
    </>
  );
};
export default App;
